func main() {
	var out strings.Builder
	lines := strings.Split(ERRORS, "\n")
	for _, line := range lines {
		kv := strings.Split(line, "\t")
		out.WriteString(fmt.Sprintf("%s = \"%s\"\n", strcase.ToCamel(kv[1]), kv[0]))
	}
	fmt.Println(out.String())
}
